const fs = require("fs");
const path = require("path");

// Base64 string (example, replace with actual data)
 const base64String = "";

// Convert Base64 to buffer
const imageBuffer = Buffer.from(base64String, "base64");

// Define the path to the frontend folder
const filePath = path.join(__dirname, "../Frontend/public/output.jpg");

// Ensure the parent directory exists (not needed if frontend already exists)
fs.mkdirSync(path.dirname(filePath), { recursive: true });

// Save the image in the frontend folder
fs.writeFileSync(filePath, imageBuffer);

console.log("Image saved at:", filePath);

